// TextToSpeech.js (Inside your voice folder)
const TextToSpeech = (text) => {
  const speech = new SpeechSynthesisUtterance(text);
  speech.lang = "en-US";  // Set the language to English. You can adjust to other languages if needed.
  
  // Get all available voices
  const voices = speechSynthesis.getVoices();
  
  // Select a female voice. Adjust as necessary based on what is available in your browser.
  const femaleVoice = voices.find(voice => voice.name.toLowerCase().includes("female"));

  // If a female voice is found, set it, otherwise, it will default to the first available voice
  if (femaleVoice) {
    speech.voice = femaleVoice;
  } else {
    // Default to the first available voice if no female voice is found
    speech.voice = voices[0];
  }

  speech.volume = 1;  // Max volume
  speech.rate = 1;    // Normal speed
  speech.pitch = 1;   // Normal pitch

  speechSynthesis.speak(speech);
};

export default TextToSpeech;



