import React, { useState } from "react";
import { FaMicrophone } from "react-icons/fa";
import "./css/ChatInput.css";
import { ReactComponent as Send } from '../asset/send.svg';

const ChatInput = ({ onSendMessage }) => {
  const [input, setInput] = useState("");

  const handleSend = () => {
    if (input.trim()) {
      onSendMessage(input);
      setInput("");
    }
  };

  return (
    <div className="chat-input">
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Type a message..."
        className="input-field"
      />
      <button onClick={handleSend} className="send-button"><svg fill="" width="64px" height="64px" viewBox="-2.4 -2.4 28.80 28.80" id="up-arrow-circle" data-name="Flat Color" xmlns="http://www.w3.org/2000/svg" class="icon flat-color" stroke="" transform="matrix(1, 0, 0, 1, 0, 0)" stroke-width="0.00024000000000000003"><g id="SVGRepo_bgCarrier" stroke-width="0"></g><g id="SVGRepo_tracerCarrier" stroke-linecap="round" stroke-linejoin="round" stroke="#CCCCCC" stroke-width="0.096"></g><g id="SVGRepo_iconCarrier"><circle id="primary" cx="12" cy="12" r="10" style="fill: #676767;"></circle><path id="secondary" d="M14.83,9.5,12.69,6.38a.82.82,0,0,0-1.38,0L9.17,9.5A1,1,0,0,0,9.86,11H11v6a1,1,0,0,0,2,0V11h1.14A1,1,0,0,0,14.83,9.5Z" style="fill: #6767672ca9bc;"></path></g></svg></button>
      <button className="mic-button"><FaMicrophone /></button>
    </div>
  );
};

export default ChatInput;
