import React, { useEffect, useRef, useImperativeHandle, useState } from "react";
import "./css/ChatDisplay.css";
// Import the image and TTS function
import botIcon from '../asset/ai.png';

// Add the SpeechSynthesis API function
const speakText = (text) => {
  const speech = new SpeechSynthesisUtterance(text);
  speech.voice = speechSynthesis.getVoices().find(voice => voice.name === "Google UK English Female"); // Female voice
  speechSynthesis.speak(speech);
};

const ChatDisplay = React.forwardRef(({ messages, currentResponseIndex }, ref) => {
  const [showExpectedResponse, setShowExpectedResponse] = useState(null); // Track which bot message has an expected response
  const chatEndRef = useRef(null);
  const messageRefs = useRef([]);
  const containerRef = useRef(null);

  // Update message refs whenever messages change
  useEffect(() => {
    messageRefs.current = messageRefs.current.slice(0, messages.length);
  }, [messages]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, currentResponseIndex]);

  // Smooth scroll to the target message index
  const scrollToMessage = (index) => {
    if (messageRefs.current[index] && containerRef.current) {
      const container = containerRef.current;
      const messageElement = messageRefs.current[index];
      const offsetTop = messageElement.offsetTop - container.offsetTop;
      const centerOffset = offsetTop - container.clientHeight / 2 + messageElement.clientHeight / 2;
      container.scrollTo({ top: centerOffset, behavior: "smooth" });
    }
  };

  const handleGenerateResponse = (index) => {
    setShowExpectedResponse(index);
    setTimeout(() => {
      setShowExpectedResponse(null); // Hide expected response after a short delay
    }, 3000);
  };

  const handleSpeakerClick = (responseText) => {
    speakText(responseText); // Speak out the response
  };

  useImperativeHandle(ref, () => ({
    scrollToMessage,
  }));

  return (
    <div ref={containerRef} className="chat-display">
      {messages.map((msg, index) => (
        <div
          key={index}
          ref={(el) => (messageRefs.current[index] = el)}
          className={`message ${msg.sender} ${index === currentResponseIndex ? "highlighted" : ""}`}
        >
          {msg.sender === "bot" && (
            <span className="bot-icon">
              <img 
                src={botIcon} 
                alt="Chatbot Icon" 
                style={{ width: "50px", height: "52px", paddingBottom: "10px" }} 
              />
            </span>
          )}
          <span className="message-text" dangerouslySetInnerHTML={{ __html: msg.text }}></span>

          {/* "Generate Reply" button */}
          {msg.sender === "bot" && !showExpectedResponse && (
            <button className="generate-reply" onClick={() => handleGenerateResponse(index)}>
              Generate Reply
            </button>
          )}

          {/* Display Expected Reply */}
          {showExpectedResponse === index && (
            <div className="expected-response">
              <p><strong>Expected Reply:</strong> {msg.expectedResponse}</p>
            </div>
          )}

          {/* Speaker button for text-to-speech */}
          {msg.sender === "bot" && (
            <button className="speak-button" onClick={() => handleSpeakerClick(msg.text)}>
              üéôÔ∏è
            </button>
          )}
        </div>
      ))}
      <div ref={chatEndRef} />
    </div>
  );
});

export default ChatDisplay;
