import React, { useState } from "react";
import { FaMicrophone } from "react-icons/fa";
import axios from "axios"; // Import axios
import "./css/ChatInput.css";

const ChatInput = ({ onSendMessage }) => {
  const [input, setInput] = useState("");

  const handleSend = async () => {
    if (input.trim()) {
      // Send the message to the parent component
      onSendMessage(input);

      // Make a POST request to the API
      try {
        const response = await axios.post("http://localhost:5000/api/chat/generate", {
          prompt: input
        });

        // Handle the response from the API
        const botResponse = response.data; // Adjust this based on your API response structure
        onSendMessage(botResponse.text); // Send the bot response to the parent component
      } catch (error) {
        console.error("Error sending message:", error);
      }
      
      // Clear the input field
      setInput("");
    }
  };

  return (
    <div className="chat-input">
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Type a message..."
        className="input-field"
      />
      <button onClick={handleSend} className="send-button">
        <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path d="M12 7V17M12 7L16 11M12 7L8 11M21 12C21 16.9706 16.9706 21 12 21C7.02944 21 3 16.9706 3 12C3 7.02944 7.02944 3 12 3C16.9706 3 21 7.02944 21 12Z" stroke="#2f2f2f" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"></path>
        </svg>
      </button>
      <button className="mic-button"><FaMicrophone /></button>
    </div>
  );
};

export default ChatInput;
